mapper.select:
select id AS "id",
app AS "app",
name AS "name",
description AS "desc",
created_at AS "createdAt",
updated_at AS "updatedAt"
from h_roles

    and app = #{app}


        and name like #{nameLike}



        and description like #{descLike}

mapper.update:
update h_roles


        name = #{name},


        description = #{desc},

    updated_at=#{updatedAt}
    where id=#{id}
    and app=#{app}

mapper.delete:
delete
from h_roles
where id = #{id}
  and app = #{app}

mapper.select:
select a.menu_id AS "key",
       b.name    AS "label",
       b.url     AS "url"
from h_role_menus a,
     h_menus b
where a.menu_id = b.id
  and a.app = b.app
  and b.app = #{app}
  and a.role_id = #{id}
order by b.created_at asc

select a.id AS "id", a.app AS "app", a.username AS "username", a.password AS "password", a.role_id AS "roleId", b.name AS "roleName", a.created_at AS "createdAt", a.updated_at AS "updatedAt" from h_users a,h_roles b
a.role_id=b.id and a.app=b.app and a.app = #{app}
and username like #{usernameLike}
and role_id = #{roleId}

mapper.update:
update h_users


        password = #{password},


        role_id = #{roleId},

    updated_at=#{updatedAt}
    where id=#{id}
    and app=#{app}

mapper.delete:
delete
from h_users
where id = #{id}
  and app=#{app}

delete
from h_users
where role_id = #{roleId}
  and app = #{app}

mapper.select:
select id AS "id", app AS "app", name AS "name", url AS "url", parent_id AS "parentId", menu_level AS "menuLevel", order_index AS "orderIndex", icon_name AS "iconName", created_at AS "createdAt", updated_at AS "updatedAt" from h_menus
and app=#{app}
and name like #{nameLike}
and url like #{urlLike}
order by created_at asc

mapper.update:
update h_menus
set name        = #{name},
    url         = #{url},
    parent_id   = #{parentId},
    menu_level  = #{menuLevel},
    icon_name   = #{iconName},
    order_index = #{orderIndex},
    updated_at=#{updatedAt}
where id = #{id}
  and app=#{app}

mapper.delete:
delete
from h_menus
where id = #{id}
  and app=#{app}

delete
from h_role_menus
where role_id = #{roleId}
  and app = #{app}

delete
from h_role_menus
where menu_id = #{menuId}
  and app = #{app}

mapper.update:
update h_users
set password=#{modify.newPassword}
where id = #{modify.id}
  and app=#{app}
