mapper.select:
select id AS "id", name AS "name", description AS "desc", created_at AS "createdAt", updated_at AS "updatedAt" from h_roles
and name like #{nameLike}
and description like #{descLike}

mapper.update:
update h_roles


        name = #{name},


        description = #{desc},

    updated_at=#{updatedAt}
    where id=#{id}

mapper.select:
select a.id AS "id", a.username AS "username", a.password AS "password", a.role_id AS "roleId", b.name AS "roleName", a.created_at AS "createdAt", a.updated_at AS "updatedAt" from h_users a,h_roles b
a.role_id=b.id
and username like #{usernameLike}
and role_id = #{roleId}

mapper.update:
update h_users


        password = #{password},


        role_id = #{roleId},

    updated_at=#{updatedAt}
    where id=#{id}

mapper.select:
select id AS "id", name AS "name", url AS "url", parent_id AS "parentId", order_index AS "orderIndex", created_at AS "createdAt", updated_at AS "updatedAt" from h_menus
and name like #{nameLike}
and url like #{urlLike}

mapper.update:
update h_menus


        name = #{name},


        url = #{url},

    updated_at=#{updatedAt}

where id=#{id}

